achievements_screen.content:
{{#if achievements.length}}
    {{#each achievements}}
        <div class="achievement-item">
            <div class="achievement-marker"></div>
            <div class="achievement-content">
                <p class="achievement-title">{{this.title}}</p>
                <p class="achievement-description">{{this.description}}</p>
            </div>
        </div>
    {{/each}}
{{else}}
    <p class="story-paragraph">No achievements unlocked yet. Explore the world to discover achievements.</p>
{{/if}}

saves_screen.content:
{{#each saveSlots}}
    <div class="save-slot">
        <div>
            <strong>Slot {{@index}}</strong><br>
            {{this.info}}
        </div>
        <div>
            <button class="save-slot-button" data-action="save" data-slot="{{@index}}">Save</button>
            {{#if this.exists}}
                <button class="save-slot-button" data-action="load" data-slot="{{@index}}">Load</button>
            {{/if}}
        </div>
    </div>
{{/each}}
