journal_screen.content:
{{#if journal.length}}
    {{#each journal}}
        <div style="margin-bottom: 20px; padding-bottom: 10px; border-bottom: 1px solid var(--divider-color);">
            <p class="story-paragraph">{{this.text}}</p>
            <p style="font-size: 0.8em; color: var(--accent-color);">{{this.timestamp}}</p>
        </div>
    {{/each}}
{{else}}
    <p class="story-paragraph">Your journal is empty. Notes will appear here as you discover important information.</p>
{{/if}}

achievements_screen.content:
{{#if achievements.length}}
    {{#each achievements}}
        <div style="margin-bottom: 15px; padding: 10px; background-color: rgba(255, 255, 255, 0.05); border-radius: 4px;">
            <p style="font-weight: bold; color: var(--accent-color);">{{this}}</p>
        </div>
    {{/each}}
{{else}}
    <p class="story-paragraph">No achievements unlocked yet. Explore the world to discover achievements.</p>
{{/if}}

saves_screen.content:
{{#each saveSlots}}
    <div class="save-slot">
        <div>
            <strong>Slot {{@index}}</strong><br>
            {{this.info}}
        </div>
        <div>
            <button class="save-slot-button" data-action="save" data-slot="{{@index}}">Save</button>
            {{#if this.exists}}
                <button class="save-slot-button" data-action="load" data-slot="{{@index}}">Load</button>
            {{/if}}
        </div>
    </div>
{{/each}}
